#include <iostream>
#include <vector>

using namespace std;

class Solution {
public:
    bool isSubsequence(string s, string t);
};


bool Solution::isSubsequence(string s, string t)
{
	int tl = t.length();
	int l = 0;
	string r;

    if (s.length() == 0 && t.length() == 0)
        return true;

	for (int i = 0; i < tl; i++) {
		if (s[l] == t[i])
			l++;

		if (l == s.length())
			return true;
	}

	return false;
}

#define result(x) ((x.ao == x.eo) ? "pass" : "fail")

struct ti {
	string t;
	string s;
	bool eo;
	bool ao;
};

vector<ti> test = {
		{
				.t = "ahbgdc",
				.s = "abc",
				.eo = true,
		},
		{
				.t = "ahbgdc",
				.s = "axc",
				.eo = false,
		},
		{
				.t = "baab",
				.s = "ab",
				.eo = true,
		},
		{
				.t = "",
				.s = "leeeeetcode",
				.eo = true,
		},
};

int main()
{
	Solution* s = new Solution();

	for (int i = 0; i < test.size(); i++) {
		test[i].ao = s->isSubsequence(test[i].s, test[i].t);
		cout << i << " -- " << result(test[i]) << endl;
	}

	return 0;
}
